import React, { useEffect } from 'react';
import { View, Text, StyleSheet, Image, Button } from 'react-native';
import * as Font from 'expo-font';

export default function App() {
  useEffect(() => {
    async function loadFonts() {
      await Font.loadAsync({
        'Roboto-Regular': require('./assets/fonts/Roboto-Regular.ttf'), // Adjust the path to your font file
      });
    }

    loadFonts();
  }, []);

  return (
    <View style={styles.container}>
      <Image
        source={require('./assets/logo.png')}
        style={styles.logo}
      />
      <Text style={styles.welcomeText}>Welcome to DareYou</Text>
      <Text style={styles.title}>Existing Users</Text>
      <Button title="Login" onPress={() => console.log('Login pressed')} />

      <Text style={styles.title}>New Users</Text>
      <Button title="Register" onPress={() => console.log('Register pressed')} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#001274',
  },
  logo: {
    width: 100,
    height: 100,
    marginBottom: 20,
  },
  welcomeText: {
    color: 'white',
    fontFamily: 'Roboto-Regular',
    fontSize: 20,
  },
  title: {
    fontSize: 18,
    marginTop: 20,
    fontFamily: 'Roboto-Regular',
    color: 'white',
  },
});
